      )
{%- if Data['anyInputSubs'] %}
AND S.Name IN ('{{Data.anyInputSubs | map: "Subscription: Name" | join: "' , '" }}')
{% endif -%}
{%- if Data.Workflow.skipContractEffDateValidation == 'SKIP' %}
AND TermEndDate >= DATE_PARSE('{{Data.Liquid.contractEffectiveOn}}', '%Y-%m-%d') 
