parameters.raw_body:
{%- assign contractEffectiveOn = Data.Liquid.contractEffectiveOn -%}
{%- if Data.LinkData.needsTermAlign == 'true' -%}
{%- comment -%}Align the contractEffective to the End of Term{%- endcomment -%}
   {%- assign contractEffectiveOn = Data.LinkData.TermEndDate -%}
{%- endif -%}

{%- comment -%}Either use the input parameters to set applicability OR use the configured one{%- endcomment -%}
{%- assign applyDiscountTo = "" -%}
{%- if Data.Workflow['toOTC?'] -%}
   {%- assign applyDiscountTo = applyDiscountTo | append: "ONETIME" -%}
{%- endif -%}
{%- if Data.Workflow['toRecurring?'] -%}
   {%- assign applyDiscountTo = applyDiscountTo | append: "RECURRING" -%}
{%- endif -%}
{%- if Data.Workflow['toUsage?'] -%}
   {%- assign applyDiscountTo = applyDiscountTo | append: "USAGE" -%}
{%- endif -%}
{%- assign applyDiscountTo = applyDiscountTo | default: Data.theGoodwillRatePlanCharge[0].ApplyDiscountTo -%}

{%- comment -%}Use the input param for Up to periods, OR use the configured one{%- endcomment -%}
{%- if Data.RatePlanCharge.UpToPeriods > 0 -%}
   {%- assign upToNumPeriods = Data.RatePlanCharge.UpToPeriods -%}
{%- else -%}
   {%- assign upToNumPeriods = Data.theGoodwillRatePlanCharge[0].UpToPeriods -%}
{%- endif -%}

{%- comment -%}Sets the appropriate discount end conditions depending on which tenant capability{%- endcomment -%}
{%- if Data.Liquid.amendUrl == "orders" %}
  {%- if Data.Workflow['useFixedPeriod?'] -%}
     {%- capture theEndOfIt -%}
      "endDate": {
        "endDateCondition": "Fixed_Period",
        "upToPeriodsType": "Billing_Periods",
        "upToPeriods": {{upToNumPeriods}}
      },
     {%- endcapture -%}
  {%- else -%}
     {%- capture theEndOfIt -%}
      "endDate": {
        "endDateCondition": "Subscription_End"
      },
     {%- endcapture -%}
  {%- endif -%}
{%- else -%}
  {%- if Data.Workflow['useFixedPeriod?'] -%}
     {%- capture theEndOfIt -%}
      "EndDateCondition": "FixedPeriod",
      "UpToPeriodsType": "Billing Periods",
      "UpToPeriods": {{upToNumPeriods}},
     {%- endcapture -%}
  {%- else -%}
     {%- capture theEndOfIt -%}
      "EndDateCondition": "SubscriptionEnd",
     {%- endcapture -%}
  {%- endif -%}
{%- endif -%}

{%- comment -%}Applicable payload for either Orders tenant OR Amendments tenant{%- endcomment -%}
{%- if Data.Liquid.amendUrl == "orders" -%}
{
  "orderDate": "{{ 'now' | date: '%Y-%m-%d' }}",
	"existingAccountNumber": "{{Data.LinkData.AccountNumber}}",
  "subscriptions": [
    {
      "subscriptionNumber": "{{Data.LinkData.Name}}",
      "orderActions": [
        {
          "type": "AddProduct",
          "addProduct": {
            "productRatePlanId": "{{Data.theGoodwillRatePlan[0].Id}}",
            "chargeOverrides" : [ {
              "productRatePlanChargeId": "{{Data.theGoodwillRatePlanCharge[0].Id}}",
              {{theEndOfIt}}
              "pricing": {
                 "discount": {
                    "applyDiscountTo": "{{applyDiscountTo}}"             
                  }
              }
            }]
          },
          "triggerDates": [
            {
              "name": "ContractEffective",
              "triggerDate": "{{contractEffectiveOn}}"
            },
            {
              "name": "ServiceActivation",
              "triggerDate": "{{contractEffectiveOn}}"
            },
            {
              "name": "CustomerAcceptance",
              "triggerDate": "{{contractEffectiveOn}}"
            }
          ]
        }      
      ]
    }
  ]
}
{%- else -%}
{
  "requests": [
    {
      "Amendments": [
