parameters.raw_body:
{% assign total_balance = Data.Liquid.InvoiceAmount -%}
{% assign totalBeforeRounding = total_balance | times: Data.Workflow.percentFee -%}
{% assign totalCost = totalBeforeRounding | round: 2 -%}
{
  "existingAccountNumber": "{{Data.Liquid.AccountNumber}}",
  "orderDate": "{{Data.Workflow.ExecutionDate}}",
  "subscriptions": [
    {
      "orderActions": [
        {
          "createSubscription": {
            "subscribeToRatePlans": [
              {
                "chargeOverrides": [
                  {
                    "productRatePlanChargeId": "{{Data.Workflow.ChargeID}}",
                    "pricing": {
                      "oneTimeFlatFee": {
                        "listPrice": "{{totalCost}}"
                      }
                    }
                  }
                ],
                "productRatePlanId": "{{Data.Workflow.PlanID}}"
              }
            ],
            "terms": {
              "initialTerm": {
                "startDate": "{{Data.Workflow.ExecutionDate}}",
                "termType": "EVERGREEN"
              }
            }
          },
          "triggerDates": [
            {
              "name": "ServiceActivation",
              "triggerDate": "{{Data.Workflow.ExecutionDate}}"
            },
            {
              "name": "CustomerAcceptance",
              "triggerDate": "{{Data.Workflow.ExecutionDate}}"
            }
          ],
          "type": "CreateSubscription"
        }
      ]
    }
  ]
}

