payload.css:
body, td, input, textarea, select {
  font: 12px/16px Verdana, Arial, Helvetica, sans-serif;
  color: #000;
}

input {
  vertical-align: middle;
  -moz-border-radius: 4px 4px 4px 4px;
  -webkit-border-radius: 4px 4px 4px 4px;
  border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.1);
    height: 28px;
}

select{
    background: #fff;
    overflow: hidden;
    -moz-border-radius: 4px 4px 4px 4px;
    -webkit-border-radius: 4px 4px 4px 4px;
    border: 1px solid #ccc;
    border-radius: 4px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.1);
    height:28px;
}

@media (max-width: 360px) {
select{
max-width:250px;
}
}

input.hint {
  color: #888;
}

.form-element input{
 padding:0 4px;
}

.form-element-date {
  width: 94px;
  height: 25px;
  text-align: right;
}

label {
  display: block;
  
  vertical-align: middle;
  width: 100%;
  padding-left:6px;
}



@media (min-width: 768px) {
label {
	display:inline-block;
	width:30%;
	max-width:280px;
	}
}

h3 {
  color: #808080;
}

h2, label {
  color: #808080;
}

body {
	display: block;
	margin: 1px;
}

.form {
  padding: 20px 16px;
  margin: 0 auto;
}



@media (min-width: 768px) {
.form {
  min-width: 100%;}
}

.invalid {
  border: 1px solid #ED1C24;
}

.form img {
  vertical-align: middle;
}

.form-group {
  margin-bottom: 10px;
}

.form-element {
  display: inline-block;
  border-left: 2px solid #FFF;
  padding-left: 1px;
  border-left-color: rgba(255, 255, 255, 0);
  min-width:270px;
}

.form-element.required {
  border-left: 4px  solid #6EC5AB;
  vertical-align: middle;
  height:28px;
  padding:2px;
}

.form-element input{
 padding:0 2px;
}

.form-element-date {
  width: 94px;
  height: 25px;
  text-align: right;
}

.form-group .error {
  margin: 4px 0 5px 140px;
}

.popup {
  background-color: #FAFAFA;
}

.popup .form {
  margin-top: 1px;
  padding: 0;
  width: 100%;
  -webkit-border-radius: 8px;
  -moz-border-radius: 8px;
  border-radius:8px;
}



.popup .form h2 {
  padding: 10px 17px;
  margin:0 auto;
  margin-top: 1px;
  background-color: #EDEDED;
  border-bottom: 1px solid #d5d5d5;
}

.whitespace {
  padding: 15px 17px;
  border-bottom: 0px;
}

.popup .form h3 {
  padding: 10px 25px;
  margin-top: 0;
  border-bottom: 0px;
}

.popup .form .content {
  padding: 0 20px 20px 20px;
  margin: 0 auto;
}

.popup .form #btn-submit {
  margin-right: 200px;
}


a { 
	text-decoration:none;
}

a.modalCloseImg {
  background: url(Images/hpm/icon-close.gif) no-repeat;
  width: 7px;
  height: 7px;
  display: inline;
  z-index: 3200;
  position: absolute;
  top: 13px;
  right: 13px;
  cursor: pointer;
}

.required-desc {
  width: 24%;
  float: right;
  margin-top: 11px;
  border-left: 4px solid #6EC5AB;
  vertical-align: middle;
  min-width:120px;
}

@media (min-width: 768px) {
.required-desc {
  width: 30%;
  float: left;
  max-width:280px;
  }
}
@media (max-width: 360px) {
.required-desc {
float:none;
margin:0 0 16px 5px;
width:100%;
}
}^

.spacer{
	padding:2px;
}

#btn-submit {
  float: right;
  margin-right: 150px;
}

.error {
  font-size: 12px;
  color: #ED1C24;
  margin: -8px 0 5px 0;
}

.error-field {
  font-size: 12px;
  color: #ED1C24;
  margin: 4px 0 5px 10px;
}

.date-separator{
  font-size: 12px;
}

.bottom {
  margin-top: 15px;
  overflow: hidden;
}

.form .desc {
  margin-bottom: 15px;
  margin-top: 5px;
}

.popup .form .desc {
  margin-left: 25px;
  margin-right: 37px;
}

.popup .form #btn-submit {
  margin-right: 27px;
}

.tooltip-text{
	font-size: 12px;
}

a.tooltip{
display:none;
}

.image-cvv{
	cursor: pointer; 
	top: -3px;
}

.image-cvv-position{
position: relative; 
}

.image-cvv-des {
	position: absolute; 
	top: 28px; 
	left: 120px; 
	z-index: 20; 
	border: 1px solid rgb(232, 232, 232);
}

.btn-submit {
  float: left;
  margin-left: 5px;
  display: inline-block;
  padding: 9px 20px;
  color: white;
  font-weight: bold;
  font-size: 12px;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
  *zoom: 1;
  filter: progid:DXImageTransform.Microsoft.gradient(gradientType=0, startColorstr='#FFC1DB9B', endColorstr='#FF8BBF4B');
  background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #6EC5AB), color-stop(100%, #6EC5AB));
  background-image: -webkit-linear-gradient(#6EC5AB, #6EC5AB);
  background-image: -moz-linear-gradient(#6EC5AB, #6EC5AB);
  background-image: linear-gradient(#6EC5AB, #6EC5AB);
}

.btn-submit.disabled {
  *zoom: 1;
  filter: progid:DXImageTransform.Microsoft.gradient(gradientType=0, startColorstr='#FFE2E3E3', endColorstr='#FFC9CACA');
  background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #e2e3e3), color-stop(100%, #c9caca));
  background-image: -webkit-linear-gradient(#e2e3e3, #c9caca);
  background-image: -moz-linear-gradient(#e2e3e3, #c9caca);
  background-image: linear-gradient(#e2e3e3, #c9caca);
  pointer-events: none; 
  cursor: default;
}

@media (min-width: 768px) {
.btn-submit {
  float: left;
  margin-left: 12px;
}
}

payload.mandatePageTemplate:
#set($creditor_id = "Your Creditor ID (e.g. GR16 0110 1250 0000 0001 2300 695)")
#if(!$myCreditorID)
    #set($myCreditorID = "Creditor ID")
#end
#if(!$myCompanyName)
    #set($myCompanyName = "Name")
#end
#if(!$myCompanyAddress)
    #set($myCompanyAddress = "Address")
#end
#if(!$customerAchAccountNum)
    #set($customerAchAccountNum= "Account Number")
#end
#if(!$customerAchAbaNum)
    #set($customerAchAbaNum= "Routing Number")
#end
#if(!$customerName)
    #set($customerName = "Name")
#end
#if(!$customerAddress)
    #set($customerAddress = "Address")
#end
#if(!$mandateReference)
    #set($mandateReference = "Reference")
#end
#if(!$confirmationDate)
    #set($confirmationDate = "Date")
#end
#if(!$prefixTerms)
    #set($prefixTerms = "<p>By signing this mandate form, you authorise (A) <strong> Example Company </strong> to send instructions to your bank to debit your account and (B) to debit your account in accordance with the instruction from <strong> Example Company </strong>.</p><p>If an error is made in the payment of your ACH Debit, by Example Company or your bank or building society, you are entitled to a full and immediate refund of the amount paid from your bank or building society. If you receive a refund you are not entitled to, you must pay it back when Example Company asks you to. You can cancel a ACH Debit at any time by simply contacting your bank or building society. Written confirmation may be required. Please also notify us.</p>")
#end
#if(!$confirmationButton)
    #set($confirmationButton = "Confirm")
#end
#if(!$cancelButton)
    #set($cancelButton = "Cancel")
#end
#if(!$suffixTerms)
    #set($suffixTerms = "By confirming, you agree to be pre-notified 3 working days before a debit.")
#end


<div class="container">
    <div class="heading">
        <h3>Example ACH Debit Mandate Confirmation</h3>
    </div>

    <div class="fieldGroup">
        <div class="field">
            <div class="label">$myCreditorID: </div>
            <div class="value">$creditor_id</div>
        </div>
        <div class="field">
            <div class="label">$myCompanyName: </div>
            <div class="value">Your Company Name</div>
        </div>
        <div class="field">
            <div class="label">$myCompanyAddress: </div>
            <div class="value">Your Company Address</div>
        </div>
    </div>

    <div class="fieldGroup">
        <div class="field">
            <div class="label">$customerAchAccountNum: </div>
            <div class="value">{{field_achBankAccountNumber}}</div>
        </div>
        <div class="field">
            <div class="label">$customerAchAbaNum: </div>
            <div class="value">{{field_achBankABACode}}</div>
        </div>
        <div class="field">
            <div class="label">$customerName: </div>
            <div class="value">{{field_achBankAccountName}}</div>
        </div>
        <div class="field">
            <div class="label">$customerAddress: </div>
            <div class="value">{{field_achAddress1}} {{field_achAddress2}}, {{field_achCity}}, {{field_achState}}, {{field_achCountry}}</div>
        </div>

    </div>

    <div class="fieldGroup">
        <div class="field">
            <div class="label">$mandateReference: </div>
            <div class="value">[Available after confirmation]</div>
        </div>
        <div class="field">
            <div class="label">$confirmationDate: </div>
            <div class="value">{{currentDate}}</div>
        </div>
    </div>

    <div class="section">
        $prefixTerms
        <a class="btn confirmBtn">$confirmationButton</a>
        <a class="btn cancelBtn">$cancelButton</a>
        <div style="clear: both"></div>
        <p>$suffixTerms</p>
    </div>

</div>

<style>
    .modalLayer {
        position: absolute;
        width: 100%;
        height: 100%;
        background-color: #121212;
        opacity: 0.25;
        z-index: 20;
        top: 0;
    }
    .mandatePage {
        margin-left: auto;
        margin-right: auto;
        top: 10%;
        left: 5%;
        text-align: center;
        width: 90%;
        position: fixed;
        min-height: 500px;
        background-color: #FDFDFC;
        z-index: 21;
    }

    .mandatePage.container {
        box-shadow: #323232 0 0 15px;
        border-radius: 5px;
    }

    .mandatePage .heading{
        text-align: left;
        padding: 5px;
        margin: 5px;
        background-color: rgb(243, 243, 242);
    }

    .mandatePage .fieldGroup {
        border: 2px;
        border-style: solid;
        border-color: #ccceca;
        border-radius: 2px;
        margin: 5px 5px 5px 5px ;
        overflow: auto;
    }
    .mandatePage .fieldGroup :after {
        clear: both;
    }
    .mandatePage .fieldGroup .field {
        vertical-align: middle;
        padding: 10px 0 10px 0;
        width: 500px;
    }

    .mandatePage .fieldGroup .field:last-of-type{
        padding-bottom: 20px;
    }

    .mandatePage .fieldGroup .field .label{
        color: rgba(23, 23, 22, 0.67);
        float:left;
        width:150px;
        text-align: right;
    }
    .mandatePage .fieldGroup .field .value{
        float: left;
        width: 280px;
        text-align: left;
    }
    .mandatePage .fieldGroup .field .value :after{
        clear: both;
    }

    .mandatePage .section {
        margin: 5px 5px 5px 5px;
    }

    .mandatePage .section p {
        text-align: left;
        /*color:red;*/
        color: rgba(23, 23, 22, 0.67);
    }

    .mandatePage .btn {
        display: block;
        text-align: center;
        border-radius: 5px;
        opacity: 0.75;
        padding: 15px 0px 15px 0px;
        font-size: large;
        width: 45%;
        float: left;
        color: #f5f5f5;
    }

    .mandatePage .cancelBtn {
        background: #fff5c7;
        margin-left: 5%;
        color: #0A0000;
    }

    .mandatePage .confirmBtn {
        background: #50AFB1;
    }

    .mandatePage .btn:hover{
        opacity: 1;
        cursor: pointer;
        cursor: hand;
    }
</style>

