        width: 90%;
        position: fixed;
        min-height: 500px;
        background-color: #FDFDFC;
        z-index: 21;
    }

    .mandatePage.container {
        box-shadow: #323232 0 0 15px;
        border-radius: 5px;
    }

    .mandatePage .heading{
        text-align: left;
        padding: 5px;
        margin: 5px;
        background-color: rgb(243, 243, 242);
    }

    .mandatePage .fieldGroup {
        border: 2px;
        border-style: solid;
        border-color: #ccceca;
        border-radius: 2px;
        margin: 5px 5px 5px 5px ;
        overflow: auto;
    }
    .mandatePage .fieldGroup :after {
        clear: both;
    }
    .mandatePage .fieldGroup .field {
        vertical-align: middle;
        padding: 10px 0 10px 0;
        width: 500px;
    }

    .mandatePage .fieldGroup .field:last-of-type{
        padding-bottom: 20px;
    }

    .mandatePage .fieldGroup .field .label{
        color: rgba(23, 23, 22, 0.67);
        float:left;
        width:150px;
        text-align: right;
    }
    .mandatePage .fieldGroup .field .value{
        float: left;
        width: 280px;
        text-align: left;
    }
    .mandatePage .fieldGroup .field .value :after{
        clear: both;
    }

    .mandatePage .section {
        margin: 5px 5px 5px 5px;
    }

    .mandatePage .section p {
        text-align: left;
        /*color:red;*/
        color: rgba(23, 23, 22, 0.67);
    }

    .mandatePage .btn {
        display: block;
        text-align: center;
        border-radius: 5px;
        opacity: 0.75;
        padding: 15px 0px 15px 0px;
        font-size: large;
        width: 45%;
        float: left;
        color: #f5f5f5;
    }

    .mandatePage .cancelBtn {
        background: #fff5c7;
        margin-left: 5%;
        color: #0A0000;
    }

    .mandatePage .confirmBtn {
        background: #50AFB1;
    }

    .mandatePage .btn:hover{
        opacity: 1;
        cursor: pointer;
        cursor: hand;
    }
</style>

